import React, { useState } from "react";
import "./App.css";
import RiddlePage from "./RiddlePage";
import { introText } from "./riddles";
import riddles from "./riddles";

const IntroCard = () => {
  return (
    <div className="card">
      <div className="card-content black-text">
        <span className="card-title">Загадки Эллы</span>
        <p>{introText}</p>
      </div>
    </div>
  );
};

const App = () => {
  const [currPage, setCurrPage] = useState(0);
  console.log(currPage)
  return (
    <div className="App">
		{currPage ? <RiddlePage num={currPage} /> : <IntroCard />}

        <ul className="pagination">
          <li className={currPage ? "waves-effect" : "disabled" }>
            <a href="" onClick={() => setCurrPage(currPage - 1)}>
              <i className="material-icons">chevron_left</i>
            </a>
          </li>
          <li className={!currPage && 'active'}>
            <a href="" onClick={() => setCurrPage(0)}>Intro</a>
          </li>

          {[...Array(riddles.length)].map((e, i) => (
            <li className={currPage === i + 1 ? "disabled" : "waves-effect"} key={i+1}>
              <a href="">{i + 1}</a>
            </li>
          ))}

          <li className={currPage === riddles.length ? "disabled" : "waves-effect"}>
            <a href="" onClick={() => setCurrPage(currPage + 1)}>
              <i className="material-icons">chevron_right</i>
            </a>
          </li>
        </ul>
    </div>
  );
};

export default App;
